from django import template
from django.forms.boundfield import BoundField

register = template.Library()

# Filtro para obtener un ítem de un diccionario
@register.filter(name='get_item')
def get_item(dictionary, key):
    if isinstance(dictionary, dict):
        return dictionary.get(key)
    return None

# Filtro para agregar clases CSS a los campos de formularios
@register.filter(name='add_class')
def add_class(field, css_class):
    # Verificar si el campo es un campo de formulario válido (BoundField)
    if isinstance(field, BoundField):
        return field.as_widget(attrs={"class": css_class})
    # Si no es un campo de formulario, devuelve el campo original
    return field